// Create a client instance, we create a random id so the broker will allow multiple sessions
import jQuery from 'jquery';
import Paho from 'paho-mqtt';

class Mqtt extends React.Component {

  constructor(props) {
    super(props);
    // this.state = {value: ''};
    // this.handleChange = this.handleChange.bind(this);
    // this.handleSubmit = this.handleSubmit.bind(this);
    var client = new Paho.Client("broker.mqttdashboard.com", 8000, "clientId" + Mqtt.makeid(3) );
    client.connect({onSuccess:Mqtt.onConnect});
    // set callback handlers
    // client.onConnectionLost = Mqtt.onConnectionLost;
    // client.onMessageArrived = Mqtt.onMessageArrived;
  }

  // called when the client connects
  onConnect() {
    // Once a connection has been made report.
    console.log("Connected");
  }



}
export default Mqtt;
