// Create a client instance, we create a random id so the broker will allow multiple sessions
import jQuery from 'jquery';
import Paho from 'paho-mqtt';

const Mqtt ={

vars:{
client : new Paho.Client("broker.mqttdashboard.com", 8000, "clientId" + Mqtt.makeid(3) )

// set callback handlers
// client.onConnectionLost = Mqtt.onConnectionLost
// client.onMessageArrived = Mqtt.onMessageArrived

// connect the client
},

init:function(){
  client.connect({onSuccess:Mqtt.onConnect})
},

getByValue: function(variable){
  var x = document.getElementById(variable)
  return x
},

placeOrder: function(){
  console.log("Hello");
  var text = "";
	var newOrder = {
  order_id: "O" + Mqtt.makeid(7),
  // order_status: getByValue("Name"),
    user: {
		    user_name: "Oli"
     //x.elements[3].value,
  	// delivery_address: x.elements[4].value,
  	// delivery_coordinates: x.elements[5].value
	 },
  }
  // document.getElementById("order").innerHTML = JSON.stringify(newOrder);
	// document.getElementById("status").innerHTML = newOrder.order_status;
	Mqtt.onSubmit(JSON.stringify(newOrder));
},

// called when the client connects
onSubmit: function(payload) {
  // Once a connection has been made, make a subscription and send a message.
  console.log("onSubmit");
  client.subscribe("food_orders");
  var message = new Paho.Message(payload);
  message.destinationName = "food_orders";
  client.send(message);
},


// called when the client connects
onConnect: function() {
  // Once a connection has been made report.
  console.log("Connected");
},

// called when the client loses its connection
onConnectionLost: function(responseObject) {
  if (responseObject.errorCode !== 0) {
    console.log("Mqtt.onConnectionLost:"+responseObject.errorMessage);
  }
},

// called when a message arrives
onMessageArrived: function(message) {
  console.log("Mgtt.onMessageArrived:"+message.payloadString);
},

// called to generate the IDs
makeid: function(length) {
   var result           = '';
   var characters       = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
   var charactersLength = characters.length;
   for ( var i = 0; i < length; i++ ) {
      result += characters.charAt(Math.floor(Math.random() * charactersLength));
   }
   return result;
}

}
export default Mqtt;
